/**
 * Sets a given cookie on the mocked response.
 * @example res(ctx.cookie('name', 'value'))
 */
export const cookie = (
  name: string,
  value: string,
  options?: cookieUtils.CookieSerializeOptions,
): ResponseTransformer => {
